import React, { Component } from "react";
import "./App.css";

class App extends Component {
	constructor() {
		super();
		this.state = {
			message: "hello world!",
		};
	}

	changeState = () => {
		switch (this.state.message) {
			case "hello world!":
				this.setState({
					message: "Button Pushed",
				});
				break;
			case "Button Pushed":
				this.setState({
					message: "hello world!",
				});
				break;
		}
	};

	render() {
		return (
			<div className="App">
				{this.state.message}
				<button onClick={this.changeState}>PUSH IT</button>
			</div>
		);
	}
}

export default App;
