import { Component } from "react";

class Form extends Component {
	constructor(props) {
		super(props);

		this.state = {
			name: "Jone Doe",
			phone: "(123) 123-1234",
			email: "jonedoe123@gmail.com",
			edit: false,
		};

		this.editOnClick = this.editOnClick.bind(this);
	}

	render() {
		if (this.state.edit) {
			return (
				<div>
					<form>
						<label htmlFor="nameInput">Name:</label>
						<input value={this.props.info.name} type="text" id="nameInput" />
						<label htmlFor="phoneInput">Phone:</label>
						<input value={this.props.info.phone} type="text" id="phoneInput" />
						<label htmlFor="emailInput">Email:</label>
						<input value={this.props.info.email} type="text" id="emailInput" />
						<button type="submit">Save</button>
					</form>
				</div>
			);
		} else {
			return (
				<div>
					<div>
						<p>{this.state.name}</p>
						<p>Phone: {this.state.phone}</p>
						<p>Email: {this.state.email}</p>
						<button onClick={this.editOnClick}>Edit</button>
					</div>
				</div>
			);
		}
	}
}

export default Form;
