import { Component } from "react";
import Education from "./Education";
import uniqid from "uniqid";

class EducationForm extends Component {
	constructor() {
		super();

		this.state = {
			school: {
				id: uniqid(),
				schoolName: "",
				degree: "",
				startDate: "",
				endDate: "",
				edit: false,
			},
			schools: [
				{
					id: uniqid(),
					schoolName: "Test School",
					degree: "CIS",
					startDate: "2013",
					endDate: "2015",
					edit: false,
				},
			],
			displayForm: false,
		};

		this.editOnClick = this.editOnClick.bind(this);
	}

	openForm = () => {
		this.setState({
			displayForm: true,
		});
	};

	editOnClick = (e) => {
		if (this.state.edit === true) return;
		this.setState({
			edii: true,
		});
		const schoolId = e.target.id;
	};

	handelChange = (e) => {
		for (const [key] of Object.entries(this.state.school)) {
			if (e.target.id === `${key}Input`) {
				this.setState((prevState) => ({
					school: { ...prevState.school, [key]: e.target.value },
				}));
			}
		}
	};

	onSubmitForm = (e) => {
		e.preventDefault();
		const schools = this.state.schools.concat(this.state.school);
		this.setState({
			displayForm: false,
			schools: schools,
			school: {
				id: uniqid(),
				schoolName: "",
				degree: "",
				startDate: "",
				endDate: "",
				edit: false,
			},
		});
		console.log(this.state);
	};

	render() {
		if (this.state.displayForm === true || this.state.schools.length === 0) {
			return (
				<div>
					<form onSubmit={this.onSubmitForm}>
						<label htmlFor="schoolNameInput">School:</label>
						<input
							onChange={this.handelChange}
							name="schoolName"
							//value={this.state.school}
							type="text"
							id="schoolNameInput"
						/>
						<label htmlFor="degreeInput">Degree:</label>
						<input
							onChange={this.handelChange}
							//value={this.state.degree}
							name="degree"
							type="text"
							id="degreeInput"
						/>
						<label htmlFor="startDateInput">Start Date:</label>
						<input
							onChange={this.handelChange}
							name="startDate"
							//value={this.state.startDate}
							type="text"
							id="startDateInput"
						/>
						<label htmlFor="endDateInput">End Date:</label>
						<input
							onChange={this.handelChange}
							//value={this.state.endDate}
							name="endDate"
							type="text"
							id="endDateInput"
						/>
						<button type="submit">Save</button>
					</form>
					<Education info={this.state} editOnClick={this.editOnClick} />
					<button onClick={this.openForm}>Add More</button>
				</div>
			);
		} else {
			return (
				<div>
					<Education info={this.state} editOnClick={this.editOnClick} />
					<button onClick={this.openForm}>Add More</button>
				</div>
			);
		}
	}
}

export default EducationForm;
