import { Component } from "react";
import Form from "./Form";

class General extends Component {
	constructor() {
		super();
		this.state = {
			name: "Jone Doe",
			phone: "(123) 123-1234",
			email: "jonedoe123@gmail.com",
			edit: false,
		};
	}

	editOnClick = () => {
		if (!this.state.edit) {
			this.setState({
				edit: true,
			});
		} else {
			this.setState({
				edit: false,
			});
		}
	};

	render() {
		if (this.state.edit) {
			return <Form props={this.state} />;
		} else {
			return (
				<div>
					<div>
						<p>{this.state.name}</p>
						<p>Phone: {this.state.phone}</p>
						<p>Email: {this.state.email}</p>
						<button onClick={this.editOnClick}>Edit</button>
					</div>
				</div>
			);
		}
	}
}

export default General;
