import { Component } from "react";

class General extends Component {
	constructor() {
		super();
		this.state = {
			name: "Jone Doe",
			phone: "(123) 123-1234",
			email: "jonedoe123@gmail.com",
			edit: false,
		};
	}

	editOnClick = () => {
		if (!this.state.edit) {
			this.setState({
				edit: true,
			});
		} else {
			this.setState({
				edit: false,
			});
		}
	};

	render() {
		return (
			<div>
				<div>
					<p>{this.state.name}</p>
					<p>Phone: {this.state.phone}</p>
					<p>Email: {this.state.email}</p>
					<button onClick={this.editOnClick}>Edit</button>
				</div>
			</div>
		);
	}
}

export default General;
